import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import bat from "../img/bat.gif"
import logo from "../img/logo.png"

const images = [
  { src: bat, alt: 'Image 1' },
  { src: bat, alt: 'Image 2' },
  { src: bat, alt: 'Image 3' },
  { src: bat, alt: 'Image 4' },
  { src: bat, alt: 'Image 5' },
];

const headers = [
  "https://i.pinimg.com/originals/53/bd/f3/53bdf3c62c768c92df672bb06d97df90.gif",
  "https://media.tenor.com/oXvT_cWqiqcAAAAC/vandalism-graffitibanksy.gif"
]

export default function Home() {
  const relocate = () => {
    console.log('clicked');
    window.location.href = '/product';
  };

  const [isHeaderShrunk, setIsHeaderShrunk] = useState(false);
  const [headerUrlIndex, setHeaderUrlIndex] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const newScrollTop = scrollTop + 950

      const headerHeight = document.querySelector('.App-header').clientHeight;
      setIsHeaderShrunk(newScrollTop > headerHeight);
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup event listener
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const headerStyle = isHeaderShrunk ? { height: '150px', position: 'sticky', transition: '3s', backgroundImage: `url(${headers[headerUrlIndex]})` } : { height: '1000px', backgroundImage: `url(${headers[headerUrlIndex]})` };

  useEffect(() => {
    // Alternate between header URLs every other time the website is used
    if (Math.floor(Math.random() * 2) === 0) {
      setHeaderUrlIndex(0);
    } else {
      setHeaderUrlIndex(1);
    }
  }, []);

  return (
    <div className="App">
      <header className="App-header" style={headerStyle}>
        <img src={logo} style={{ width: '40vmin', height: 'auto' }} />
      </header>
      <div className="image-grid">
        {images.map((image) => (
          <Link to="/product" onClick={relocate} key={image.alt}><img key={image.alt} src={image.src} alt={image.alt} className='img-individual' /> </Link>
        ))}
      </div>
    </div>
  );
}
