import React, { useState, useEffect } from 'react';
import './App.css'; // You need to define CSS styles in a separate file.

function App() {
  const [shrink, setShrink] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    if (window.pageYOffset > 0) {
      setShrink(true);
    } else {
      setShrink(false);
    }
  };

  return (
    <div className={`App ${shrink ? 'shrink' : ''}`}>
      <h1>James Geovanny</h1>
      <div className="pictures">
        <img src="picture1.jpg" alt="Picture 1" />
        <img src="picture2.jpg" alt="Picture 2" />
        <img src="picture3.jpg" alt="Picture 3" />
      </div>
    </div>
  );
}

export default App;
