import React , { useState, useEffect } from 'react';
import { Link } from "react-router-dom";


const images = [
    { src: 'https://via.placeholder.com/300x300', alt: 'Image 1' },
    { src: 'https://via.placeholder.com/300x300', alt: 'Image 2' },
    { src: 'https://via.placeholder.com/300x300', alt: 'Image 3' },
    { src: 'https://via.placeholder.com/300x300', alt: 'Image 4' },
    { src: 'https://via.placeholder.com/300x300', alt: 'Image 5' },
    { src: 'https://via.placeholder.com/300x300', alt: 'Image 6' },
  ];

export default function Home () {

const relocate = () => {
  console.log('clicked');
  window.location.href='/product';
}

const [isHeaderShrunk, setIsHeaderShrunk] = useState(false);

useEffect(() => {

  const handleScroll = () => {

    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const newScrollTop = scrollTop + 950

    const headerHeight = document.querySelector('.App-header').clientHeight;
    setIsHeaderShrunk(newScrollTop > headerHeight);

  };

  window.addEventListener('scroll', handleScroll);

}, []);

const headerStyle = isHeaderShrunk ? { height: '150px', position: 'sticky', transition:'3s'} : { height: '1000px' };

const [bgColor, setBgColor] = useState('rgb(255, 165, 0)');

useEffect(() => {
  const intervalId = setInterval(() => {
    setBgColor((prevColor) => {
      const currentColor = prevColor.match(/\d+/g).map(Number);
      const newColor = currentColor.map((c) => (c === 0 ? 0 : c - 1));
      return `rgb(${newColor[0]}, ${newColor[1]}, ${newColor[2]})`;
    });
  }, 10);

  return () => clearInterval(intervalId);
}, []);

const backgroundStyle = {
  backgroundColor: bgColor,
  height: '100%',
  width: '100%',
  transition: 'background-color 10s linear',
};

  return (
    <div className="App">
      <header className="App-header" style={ headerStyle }>
        <h1>James Geovanny</h1>
      </header>
      <div style={backgroundStyle}>
        <div className="image-grid">
          {images.map((image) => (
            <Link to="/product" onClick={relocate} key={image.alt}><img key={image.alt} src={image.src} alt={image.alt} className='img-individual' /> </Link>
          ))}
        </div>
      </div>
    </div>
  );

  }
