import React,{ useState, useEffect } from 'react';
import './App.css';
import './product.css';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import Home from './pages/home';
import Product from './pages/product';

function App() {
  const [isHeaderShrunk, setIsHeaderShrunk] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const newScrollTop = scrollTop + 900
      const headerHeight = document.querySelector('.App-header').clientHeight;
      setIsHeaderShrunk(newScrollTop > headerHeight);
    };

    window.addEventListener('scroll', handleScroll);

    // Check if the flag for shrinking header is set in localStorage
    const hasShrunkHeaderBefore = localStorage.getItem('hasShrunkHeaderBefore');
    if (!hasShrunkHeaderBefore) {
      setIsHeaderShrunk(true);
      // Set the flag to indicate that header has been shrunk before
      localStorage.setItem('hasShrunkHeaderBefore', true);
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const headerStyle = isHeaderShrunk ? { height: '100px', position: 'sticky', transition:'3s'} : { height: '1000px' };

  return (
    <div className="App">
      <header className="App-header" style={headerStyle}>
        <h1>James Geovanny</h1>
      </header>

      <Router>
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/product" component={Product} />
        </Switch> 
      </Router>
    </div>
  );
}

export default App;
